mapper.select:
SELECT software_history.history_id, software_history.soft_id, software_history.branch_id, history_version, history_path, upload_date, operator, appPkt_date, appPkt_new, appPkt_path, appPkt_size, appPkt_md5, server_software.name, software_branch.branch FROM software_history left join server_software on software_history.soft_id = server_software.id left join software_branch on software_history.soft_id = software_branch.soft_id and software_branch.branch_id = software_history.branch_id WHERE 1=1
AND server_software.kind = #{Kind,jdbcType=VARCHAR}
AND software_history.soft_id = #{softId,jdbcType=VARCHAR}
AND software_history.branch_id = #{branchId,jdbcType=VARCHAR}
AND history_version like concat(concat('%',#{version_name}),'%')
limit #{start},#{length}

SELECT COUNT(1) FROM software_history left join server_software on software_history.soft_id = server_software.id left join software_branch on software_history.soft_id = software_branch.soft_id and software_branch.branch_id = software_history.branch_id WHERE 1=1
AND server_software.kind = #{Kind,jdbcType=VARCHAR}
AND software_history.soft_id = #{softId,jdbcType=VARCHAR}
AND software_history.branch_id = #{branchId,jdbcType=VARCHAR}
AND history_version like concat(concat('%',#{version_name}),'%')
