mapper.select:
SELECT plugin_id, software_plugin.type, software_plugin.soft_id, server_software.name, software_branch.branch, software_history.history_version, plugin_name, software_plugin.plugin_MD5, software_plugin.upload_time, relative_path, absolute_path, software_plugin.operator, software_plugin.description, software_plugin.size FROM software_plugin LEFT JOIN server_software_kind on software_plugin.type = server_software_kind.id LEFT JOIN server_software on software_plugin.soft_id = server_software.id LEFT JOIN software_branch on software_plugin.branch = software_branch.branch_id LEFT JOIN software_history on software_plugin.version = software_history.history_id WHERE 1=1
and software_plugin.type = #{type,jdbcType=VARCHAR}
and software_plugin.soft_id = #{softId,jdbcType=VARCHAR}
and software_plugin.branch = #{branch,jdbcType=VARCHAR}
and software_plugin.version = #{version,jdbcType=VARCHAR}
and software_plugin.plugin_name like concat(concat('%',#{pluginName}),'%')
limit #{start},#{length}

SELECT COUNT(1) FROM software_plugin LEFT JOIN server_software_kind on software_plugin.type = server_software_kind.id left join server_software on software_plugin.soft_id = server_software.id left join software_branch on software_plugin.branch = software_branch.branch_id left join software_history on software_plugin.version = software_history.history_id WHERE 1=1
and software_plugin.type = #{type,jdbcType=VARCHAR}
and software_plugin.soft_id = #{softId,jdbcType=VARCHAR}
and software_plugin.branch = #{branch,jdbcType=VARCHAR}
and software_plugin.version = #{version,jdbcType=VARCHAR}
and software_plugin.plugin_name like concat(concat('%',#{pluginName}),'%')
